import "core-js/modules/web.url-search-params.delete.js";
import "core-js/modules/web.url-search-params.has.js";
import "core-js/modules/web.url-search-params.size.js";
import http from "@/services/http.js";
import TitleView from "../components/Title/TitleView.vue";
import { format } from "date-fns";
import LoadingCircle from "../components/loading/LoadingCircle.vue";
import TableNormal from "../components/Table/TableNormal.vue";
import CountChart from "../components/chards/CountChard.vue";
import SexChard from "../components/chards/SexChard.vue";
import AgeChard from "../components/chards/AgeChard.vue";
import AgeChardSplit from "../components/chards/AgeChardSplit.vue";
import CountChardSplit from "../components/chards/CountChardSplit.vue";
import BrandChard from "../components/chards/BrandChard.vue";
import FilterOwner from "../components/filter/FilterOwner.vue";
import FilterWorker from "../components/filter/FIlterWorker.vue";
import FilterUser from "../components/filter/FilterUser.vue";
import FilterVehicles from "../components/filter/FilterVehicles.vue";
import FilterService from "../components/filter/FilterService.vue";
import { useAuth } from "@/stores/auth.js";
export default {
  name: "DashBoard",
  components: {
    TitleView,
    LoadingCircle,
    TableNormal,
    CountChart,
    SexChard,
    AgeChard,
    AgeChardSplit,
    CountChardSplit,
    BrandChard,
    FilterOwner,
    FilterWorker,
    FilterUser,
    FilterVehicles,
    FilterService
  },
  data() {
    return {
      maxAge: "",
      minAge: "",
      filterName: "",
      body: {},
      loading: false,
      generate: true,
      users: [],
      headers: ["Selecione Um Relatório"],
      allArray: [],
      splitBySex: false,
      filterChecked: false,
      allChecked: true,
      sex: "A",
      startDate: "",
      endDate: "",
      userFilter: false,
      ownerFilter: false,
      carsFilter: false,
      selectedFilter: "owners",
      servicesFilter: false,
      workersFilter: false,
      brandArray: [],
      brandArrayFilter: [],
      modalFilter: true,
      openModal: true,
      newUser: {},
      modalContent: "",
      user: {},
      owner: {},
      filter: {}
    };
  },
  methods: {
    openChardModal() {
      this.modalChart = true;
      this.openModal = false;
    },
    close() {
      this.filter = {};
      this.modalContent = "", this.openModal = true;
      this.modalFilter = false;
      this.modalChart = false;
      this.generate = false;
      this.loading = false;
    },
    openFilterModal() {
      this.modalFilter = true;
      this.openModal = false;
    },
    toggleCheckbox(option) {
      if (option == "allChecked") {
        this.filterChecked = false;
        this.allChecked = true;
      }
      if (option == "filterChecked") {
        this.allChecked = false;
        this.filterChecked = true;
      }
    },
    async getResults() {
      try {
        this.loading = true;
        const {
          data
        } = await http.get("/owners-all");
        const owners = data.owners;
        await this.setUsers(owners);
        const brand = await http.post("/cars-brand");
        this.brandArray = brand.data;
        this.brandArrayFilter = brand.data;
        this.allArray = this.users;
        this.filterChar = this.users;
        this.loading = false;
      } catch (error) {
        console.error("Erro ao obter os resultados:", error);
        this.loading = false;
      }
    },
    async setUsers(array) {
      const promises = array.map(async user => {
        const body = await this.getBody(user.id);
        return {
          name: user.name,
          email: user.email,
          created_at: format(new Date(user.created_at), "dd/MM/yyyy"),
          implementar: {
            name: user.name,
            email: user.email,
            id: user.id,
            sex: user.sex,
            age: user.age,
            adress: user.adress,
            body: body
          }
        };
      });
      this.users = await Promise.all(promises);
    },
    async getBody(id) {
      try {
        const {
          data
        } = await http.get(`/cars-all/${id}`);
        return data.data;
      } catch (error) {
        console.error(`Erro ao obter body para id ${id}:`, error);
        throw error;
      }
    },
    openUserModal() {
      this.user = {}, this.modalUser = true;
      this.openModal = false;
    },
    async addUser() {
      this.loading = true;
      if (this.user.name && this.user.age && this.user.email && this.user.sex && this.user.adress) {
        try {
          await http.post("/owners-register", this.user);
          window.location.reload();
          this.loading = false;
        } catch (error) {
          this.loading = false;
        }
      }
    },
    showDetails(details) {
      this.modalContent = details;
      this.owner = {
        ...details
      };
      this.newUser = this.owner;
      this.openModal = false;
    },
    async atualizar() {
      if (JSON.stringify(this.modalContent) == JSON.stringify(this.owner)) alert("Faça uma Alteração Primeiro!");else {
        if (this.modalContent.name != this.owner.name) {
          this.newUser.name = this.owner.name;
        }
        if (this.modalContent.email != this.owner.email) {
          this.newUser.email = this.owner.email;
        }
        if (this.modalContent.sex != this.owner.sex) {
          this.newUser.sex = this.owner.sex;
        }
        if (this.modalContent.age != this.owner.age) {
          this.newUser.age = this.owner.age;
        }
        if (this.modalContent.adress != this.owner.adress) {
          this.newUser.adress = this.owner.adress;
        }
        try {
          this.loading = true;
          await http.put("/update-owner", this.newUser);
          window.location.reload();
          alert("Funcionário Atualizado com sucesso");
          this.loading = false;
        } catch (error) {
          console.log(error);
          this.loading = false;
        }
      }
    },
    async apagar() {
      this.loading = true;
      try {
        await http.delete("/delete-owner/" + this.modalContent.id, this.newUser);
        alert("Veículo Apagado com sucesso");
        this.loading = false;
        window.location.reload();
      } catch (error) {
        this.loading = false;
      }
    },
    setFilter() {
      if (this.selectedFilter == "owners") this.filterOwners();
      if (this.selectedFilter == "services") this.filterServices();
      if (this.selectedFilter == "cars") this.filterCars();
      if (this.selectedFilter == "users") this.filterUsers();
      if (this.selectedFilter == "workers") this.filterWorkers();
    },
    async setArrayOwners(array) {
      this.array = await Promise.all(array.map(async owner => {
        try {
          const body = await http.get("/cars-all/" + owner.id);
          return {
            name: owner.name,
            contServices: owner.number_services,
            contCars: body.data.count,
            created_at: format(new Date(owner.created_at), "dd/MM/yyyy")
          };
        } catch (error) {
          console.log(error);
          return {
            name: owner.name,
            contServices: owner.number_services,
            contCars: 0,
            created_at: format(new Date(owner.created_at), "dd/MM/yyyy")
          };
        }
      }));
    },
    async setArrayUsers(array) {
      this.array = array.map(user => {
        return {
          name: user.name,
          email: user.email,
          created_at: format(new Date(user.created_at), "dd/MM/yyyy")
        };
      });
    },
    async setArrayWorkers(array) {
      this.array = await Promise.all(array.map(async worker => {
        try {
          const body = await http.get("/worker/" + worker.id);
          return {
            name: worker.name,
            salary: "R$ " + worker.salary,
            contServices: body.data.count,
            created_at: format(new Date(worker.created_at), "dd/MM/yyyy")
          };
        } catch (error) {
          console.log(error);
          return {
            name: worker.name,
            salary: "R$ " + worker.salary,
            contServices: 0,
            created_at: format(new Date(worker.created_at), "dd/MM/yyyy")
          };
        }
      }));
    },
    async filterOwners() {
      if (Object.keys(this.filter).length === 0) {
        //Retornar Tudo
        this.loading = true;
        try {
          const data = await http.get("/owners-all");
          await this.setArrayOwners(data.data.owners);
          this.generate = false;
          this.loading = false;
          this.headers = ["Nome", "Quantidade de Revisões", "Quantidade de Carro", "Criado Em"];
        } catch (error) {
          console.log(error);
        }
        this.loading = false;
        this.close();
      } else {
        this.loading = true;
        try {
          const data = await http.post("/filter-owners", this.filter);
          if (data.data.owner.length == 0) {
            alert("Sua Consulta Retornou zero");
          } else {
            await this.setArrayOwners(data.data.owner);
            this.close();
            this.headers = ["Nome", "Quantidade de Revisões", "Quantidade de Carro", "Criado Em"];
          }
          this.generate = false;
        } catch (error) {
          console.log(error);
        }
        this.loading = false;
      }
    },
    async filterWorkers() {
      console.log("Iniciando Filtro de Workers");
      if (Object.keys(this.filter).length === 0) {
        this.loading = true;
        const data = await http.get("/workers-all");
        console.log(data.data);
        await this.setArrayWorkers(data.data.workers);
        this.headers = ["Nome", "Salário", "Quantidade de Serviços", "Criado Em"];
        this.close();
      } else {
        this.loading = true;
        try {
          console.log(this.filter);
          const data = await http.post("/filter-workers", this.filter);
          if (data.data.workers.length == 0) {
            alert("Sua Consulta Retornou zero");
          } else {
            await this.setArrayWorkers(data.data.workers);
            this.close();
            this.headers = ["Nome", "Salário", "Quantidade de Serviços", "Criado Em"];
          }
        } catch (error) {
          console.log(error);
        }
        this.loading = false;
      }
    },
    filterCars() {
      console.log("Iniciando Filtro de Cars");
    },
    async filterUsers() {
      console.log("Iniciando Filtro de Users");
      if (Object.keys(this.filter).length === 0) {
        try {
          this.loading = true;
          const data = await http.get("users-all");
          this.setArrayUsers(data.data.users);
          this.loading = false;
          this.headers = ["Nome", "Email", "Criado Em"];
          this.close();
        } catch (error) {
          console.log(error);
        }
      } else {
        console.log(this.filter);
      }
    },
    filterServices() {
      console.log("Iniciando Filtro de Services");
    },
    async generateReport() {
      this.loading = true;
      try {
        console.log(this.array);
        const data = await http.post("/generate-report", {
          head: this.headers,
          body: this.array,
          title: "de Funcionários",
          user: this.getName()
        }, {
          responseType: "blob"
        });
        console.log(data);
        const url = window.URL.createObjectURL(new Blob([data.data]));
        const link = document.createElement("a");
        link.href = url;
        link.setAttribute("download", "relatorio.pdf");
        document.body.appendChild(link);
        link.click();
        window.URL.revokeObjectURL(url);
        this.loading = false;
      } catch (error) {
        console.log(error.response.data);
      }
    },
    getName() {
      const auth = useAuth();
      if (auth.isAuthenticated) return auth.user.name;
    }
  },
  watch: {
    minAge(newVal) {
      this.minAge = newVal.replace(/\D+/g, "");
    },
    maxAge(newVal) {
      this.maxAge = newVal.replace(/\D+/g, "");
    },
    filterName(newVal) {
      this.filterName = newVal.replace(/\d+/g, "");
    }
  },
  mounted() {},
  computed: {
    filterChar() {
      if (this.allChecked) return this.allArray;
      if (this.filterChecked) return this.users;
      return [];
    }
  }
};
