import { createApp } from 'vue';
import App from './App.vue';
import { createPinia } from 'pinia'
import router from './router/routes';
import 'tailwindcss/tailwind.css'




const app = createApp(App);
app.config.productionTip = false;
app.config.warnHandler = () => {};
window.addEventListener('unhandledrejection', event => {
    if (event.reason.message.includes('Attempting to mutate prop')) {
      event.preventDefault();
      event.stopPropagation();
      return true;
    }
  });
  app.config.errorHandler = function (err, vm, info) {
    // Ignora erros Vue específicos (como warnings)
    if (err.code === 'ERR_VUE_INVALID_CSS_SELECTOR') {
      return;
    }

    // Registra erros em um arquivo de log
    console.error(`Erro Vue: ${err.message}`);
    // Envia o erro para um serviço de relatórios de erros
    // ...
  };
app.config.errorHandler = () => {};
app.use(createPinia())
app.use(router);
app.mount('#app');
