import { createApp } from 'vue';
import App from './App.vue';
import { createPinia } from 'pinia'
import router from './router/routes';
import 'tailwindcss/tailwind.css'




const app = createApp(App);
app.config.warnHandler = (message, _, __, error) => {
    // Verificar se a mensagem de aviso específica ocorre
    if (message.includes('Attempting to mutate prop')) {
      // Suprimir a mensagem
      return;
    }
    // Lidar com outros avisos como de costume
    console.warn(message, error);
  };

  // Configuração para suprimir erros
  app.config.errorHandler = (err, _, __) => {
    // Suprimir erros ou lidar com eles conforme necessário
    console.error(err);
  };
app.config.errorHandler = () => {};
app.use(createPinia())
app.use(router);
app.mount('#app');
